import { Button, Form, Modal } from "react-bootstrap"
import { NoteModal } from "../models/NoteModal";
import { useForm } from "react-hook-form";
import { NoteInput } from "../network/Note_api";

interface AddUpdateFormProps {
    onDismiss: () => void,
    noteToEdit?: NoteInput | null,
    onNoteSaved: (note: NoteModal) => void,
};

const AddUpdateForm = ({ onDismiss, noteToEdit }: AddUpdateFormProps) => {

    const { register, handleSubmit, formState: { errors, isSubmitting } } = useForm<NoteInput>({
        defaultValues: {
            title: '',
            text: ''
        }
    })

    return (
        <Modal show onHide={onDismiss}>
            <Modal.Header closeButton>
                <Modal.Title>
                    {
                        noteToEdit
                            ? "Update Note"
                            : "Add Note"
                    }
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label>Title</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Title"
                        />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Label>Text</Form.Label>
                        <Form.Control
                            as="textarea"
                            rows={3}
                            placeholder="Text"
                        />
                    </Form.Group>
                    <Button
                        variant="primary"
                        type="submit"
                        className="w-100"
                    >
                        Submit
                    </Button>
                </Form>
            </Modal.Body>
        </Modal>
    )
};

export default AddUpdateForm;
