import axios from "axios";
import { useEffect, useState } from "react";
import { NoteModal } from "../models/NoteModal";
import { Button, Container } from "react-bootstrap";
import NotesList from "./NotesList";
import { deleteNote } from "../network/Api";
import AddUpdateForm from "./AddUpdateForm";


const NotesView = () => {

    const [notes, setNotes] = useState<NoteModal[]>([])
    const [showAddModal, setShowAddModal] = useState(false);
    const [showUpdateModal, setShowUpdateModal] = useState(false);
    const [noteToEdit, setNoteToEdit] = useState(false);

    useEffect(() => {
        async function loadNotes() {
            const res = await axios.get("http://localhost:5000/api/notes");
            setNotes(res.data);
        }
        loadNotes();
    }, []);

    const deletenote = async (note: NoteModal) => {
        try {
            await deleteNote(note._id);
            setNotes(notes.filter(exisitingNote => exisitingNote._id !== note._id));
        } catch (error) {
            console.error(error);
            alert(error);
        }
    };

    const noteClicked = (note: any) => {
        setShowUpdateModal(true);
        setNoteToEdit(true);
        console.log(note)
    };

    return (
        <>
            <Container className='ms-auto mt-3'>
                <Button
                    variant='primary'
                    onClick={() => setShowAddModal(true)}
                >Add Note</Button>
                <NotesList
                    notes={notes}
                    onDeleteClicked={(note) => deletenote(note)}
                    onNoteClicked={(note) => noteClicked(note)}
                />
            </Container>
            {
                showAddModal &&
                <AddUpdateForm
                   onDismiss = {() => setShowAddModal(false)} 
                />
            }

            {
                showUpdateModal && 
                <AddUpdateForm 
                    onDismiss = {() => setShowUpdateModal(false)}
                />
            }
        </>
    )
};

export default NotesView;
